sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	response.setContentType('application/json');
	response.setStatus(200);
	var body ={};

		var requestBody = request.queryParams;
		var phase_2 = JSON.parse(JSON.stringify(requestBody.WorkerAttributes));
		var work_att = JSON.parse(phase_2);
		var phase_3 = JSON.parse(JSON.stringify(requestBody.TaskAttributes));
		var task_att = JSON.parse(phase_3);

		if(requestBody.EventType =="reservation.accepted" && task_att.ticket_number && task_att.ticket_number.toString().trim() !='')
			{
			var user_gr = new GlideRecord('sys_user');
			user_gr.get('email',work_att.email);

			var inc_gr = new GlideRecord('incident');
			inc_gr.addQuery('number',task_att.ticket_number.toString().trim());
			inc_gr.query();

			if(inc_gr.next())
				{
				inc_gr.correlation_id = inc_gr.correlation_id+", twilio_assigned_to: "+user_gr.sys_id;
				inc_gr.update();
			}
		}
		else if(requestBody.EventType =="reservation.accepted")
			{
			var user_gr = new GlideRecord('sys_user');
			user_gr.get('email',work_att.email);

			var inc_gr = new GlideRecord('incident');
			inc_gr.initialize();

			inc_gr.assigned_to = user_gr.sys_id;
			inc_gr.contact_type = 'twilio_flex';
			inc_gr.caller_id = task_att.user_sys_id.toString().trim();
			inc_gr.correlation_id = requestBody.TaskSid.toString().trim();
			inc_gr.insert();
		}
		else if(requestBody.EventType =="task.completed" && task_att.ticket_number && task_att.ticket_number.toString().trim() !='')
			{
			var user_gr = new GlideRecord('sys_user');
			user_gr.get('email',work_att.email);

			var inc_gr = new GlideRecord('incident');
			inc_gr.addQuery('number',task_att.ticket_number.toString().trim());
			inc_gr.query();

			if(inc_gr.next())
				{
				inc_gr.correlation_id = inc_gr.correlation_id+", call_completed";
				inc_gr.correlation_id = inc_gr.correlation_id.replace(", twilio_assigned_to: "+user_gr.sys_id, "");
				inc_gr.update();
			}
		}
		else if(requestBody.EventType =="task.completed")
			{
			var user_gr = new GlideRecord('sys_user');
			user_gr.get('email',work_att.email);

			var inc_gr = new GlideRecord('incident');
			inc_gr.addQuery('correlation_id','CONTAINS',requestBody.TaskSid.toString().trim());
			inc_gr.query();

			if(inc_gr.next())
				{
				inc_gr.correlation_id = inc_gr.correlation_id+", call_completed";
				inc_gr.correlation_id = inc_gr.correlation_id.replace(", twilio_assigned_to: "+user_gr.sys_id, "");
				inc_gr.update();
			}
		}
		response.setBody(body);
	})(request, response);
